import { SEQUENCE_REMOVAL_PATTERN } from './constants';

var STRIP_PATTERN = new RegExp('(-| )?(' + SEQUENCE_REMOVAL_PATTERN.source + ')', 'g');

export default function stripHexcode(hexcode) {
  return hexcode.replace(STRIP_PATTERN, '');
}